import * as React from "react";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import {Routes} from "../connection/routes";

import {Link, withRouter} from 'react-router-dom';
import Button from "@material-ui/core/Button";
import {AuthContext} from "../AuthProvider";
import IconButton from "@material-ui/core/IconButton";
import {ExitToApp} from "@material-ui/icons";
import Typography from "@material-ui/core/Typography";
import PropTypes from 'prop-types';
import clsx from 'clsx';

const backColor = "#dddfe7";
const buttonStyle = {
    "borderRadius":"15px",
    "background":backColor,
    "boxShadow":"15px 15px 30px #bcbec4, " +
        " -15px -15px 30px #feffff"
};

class HeaderView extends React.Component {
    create = () => {
        this.props.history.push(Routes.newsCreate);
    };

    dishes = () => {
        this.props.history.push(Routes.dishes);
    };
    logout = () => this.context.logout();

    render() {
        return (
            <AppBar style={buttonStyle} position='static'>
                <Toolbar style={{color: "blue"}}>
                    <Button  onClick={this.dishes}>
                        Menu
                    </Button>
                    {this.context.currentUser ?
                        <>
                            <Button style={{color: "red"}} onClick={this.create}>
                                New dish
                            </Button>
                            <IconButton onClick={this.logout}>
                                <ExitToApp color='secondary'>
                                </ExitToApp>
                            </IconButton>
                            <Typography>
                                Hi,{this.context.currentUser.name}!
                            </Typography>
                        </>
                        :
                        <Link to={Routes.login}>
                            <IconButton>
                                <ExitToApp color='action'/>
                            </IconButton>
                        </Link>

                    }
                </Toolbar>
            </AppBar>)

    }

}

HeaderView.contextType = AuthContext;
export default withRouter(HeaderView)
