import * as React from "react";
import DishView from "./DishView";
import {endpoints} from "../connection/endpoints";
import Container from "@material-ui/core/Container";
import FavoriteIcon from '@material-ui/icons/Favorite';
import IconButton from "@material-ui/core/IconButton";
import FavoriteBorderIcon from '@material-ui/icons/FavoriteBorder';
import Box from "@material-ui/core/Box";
import LinearProgress from "@material-ui/core/LinearProgress";
import {RestRequest} from "../connection/requests";
import {Routes} from "../connection/routes";
import {withRouter} from "react-router-dom";


class DishList extends React.Component {
    constructor(props) {
        super(props);
        this.state = {news: [], loading: false, order: true};
    }

    deleteOneNews = (element) => {
        let news = this.state.news;
        let updatedNews = news.filter((news) => !(news['_id'] === element['_id']));
        this.setState({news: updatedNews});
    };

    componentDidMount() {
        this.load(this.state.order);
    }

    load = (order) => {
        this.setState({loading: true});
        debugger
        RestRequest.get(endpoints.getDishList/* + `?sort=likes&order=${order ? 1 : -1}`*/)
            .then((response) => {
                debugger
                const news = response.data.payload;
                this.setState({loading: false, news, order});
            }).catch(reason => {
            if (reason.response.status === 401 || reason.response.status === 403) this.props.history.push(Routes.login);
        });
    };
    topLike = () => {
        this.load(!this.state.order);
    };

    render() {
        let loading = this.state.loading;
        let news = this.state.news.map((news) => {
            return <DishView deleteOne={this.deleteOneNews()} key={news['_id']} news={news}/>
        });
        return (
            <React.Fragment>
                <Container maxWidth="sm">
                    <IconButton onClick={this.topLike}>
                        {this.state.order ? <FavoriteBorderIcon/> : <FavoriteIcon/>}
                    </IconButton>
                    <Box>
                        <Container>
                            {loading ? <LinearProgress/> : news}
                        </Container>
                    </Box>
                </Container>
            </React.Fragment>)
    }

}

export default withRouter(DishList)
