import * as React from "react";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import {Routes} from "../connection/routes";

import {Link, withRouter} from 'react-router-dom';
import Button from "@material-ui/core/Button";
import {AuthContext} from "../AuthProvider";
import IconButton from "@material-ui/core/IconButton";
import {ExitToApp} from "@material-ui/icons";
import Typography from "@material-ui/core/Typography";
import PropTypes from 'prop-types';
import clsx from 'clsx';

const styles = {
    root: {
        background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
        borderRadius: 3,
        border: 0,
        color: 'white',
        height: 48,
        padding: '0 30px',
        boxShadow: '0 3px 5px 2px rgba(255, 105, 135, .3)',
    },
};

function ClassNames(props) {
    const { classes, children, className, ...other } = props;

    return (
        <Button className={clsx(classes.root, className)} {...other}>
            {children || 'class names'}
        </Button>
    );
}

ClassNames.propTypes = {
    children: PropTypes.node,
    classes: PropTypes.object.isRequired,
    className: PropTypes.string,
};

//xport default withStyles(styles)(ClassNames);

class HeaderView extends React.Component {
    create = () => {
        this.props.history.push(Routes.newsCreate);
    };

    dishes = () => {
        this.props.history.push(Routes.dishes);
    };
    logout = () => this.context.logout();

    render() {
        return (
            <AppBar position='static'>
                <Toolbar>
                    <Button  onClick={this.dishes}>
                        Menu
                    </Button>
                    {this.context.currentUser ?
                        <>
                            <Button style={{color: red}} onClick={this.create}>
                                New dish
                            </Button>
                            <IconButton onClick={this.logout}>
                                <ExitToApp color='secondary'>
                                </ExitToApp>
                            </IconButton>
                            <Typography>
                                Hi,{this.context.currentUser.name}!
                            </Typography>
                        </>
                        :
                        <Link to={Routes.login}>
                            <IconButton>
                                <ExitToApp color='action'/>
                            </IconButton>
                        </Link>

                    }
                </Toolbar>
            </AppBar>)

    }

}

HeaderView.contextType = AuthContext;
export default withRouter(HeaderView)
